package fr.dawan.syntaxe.opérateur;

public class OperateursDaffectationSimplifie {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		
		// a = a + 5; peut s'ecrire autrement 
		a += 5; // a = a +5;
		a -= 5; // a = a-5;
		a *= 5; // a = a*+;
		a /= 5; // a = a/5;
		
		//Modulo = permet d'avoir le reste de la division 
		int dividende = 23;
		int diviseur = 5;
		
		int modulo = 23%5;
		System.out.println(modulo);
		
		
		
		
		
	}

}
